def reduce(a,GFf,w=-1):
    
    
    # G = galois.GF(2**int(gf.get()))
    # gg=str((G.irreducible_poly))
    
    GFf=2 ** (int(GFf))
    P= galois.GF(GFf)
    pp=str((P.irreducible_poly))
    re=""
    begincompy=0
    for st in pp:
        if (st==','):
            break
        if (begincompy==1):
            re=re+st
        elif (begincompy==0):
            if (st=='('):
                begincompy=1
    expo=[] #array containing value of exponents that have coef=1 not 0
    List=re.split("+")
    for ele in List:
        if('^' in ele):
            temp=ele.split("^")
            expo.append(temp[1])
        elif('x' in ele):
            expo.append('1')
        elif('1' in ele):
            expo.append('0')

    decred=0 #decimal value of irredusable polinomiale
    for i in range (len(expo)):
         decred=decred+2**(int(expo[i]))
    
    q=a//decred
    bbb=a-q*decred
    if (bbb>=GFf):
        bbb=decred-bbb
    if (w==-1):
        return bbb
    else:
        updateresult(str(bbb),1)
    






def add(a,b,GF):
    t=GF
    GF=2 ** (int(GF))
    if(a>=GF):
        a=reduce(a,t)
    if(b>=GF):
        b=reduce(b,t)

    gp = galois.GF(GF)
    x=gp(a)
    y=gp(b)
    z=x+y
    updateresult(str(z))

    
 
    
