def mul(a,b,GF):
    t=GF
    GF=2 ** (int(GF))
    if(a>=GF):
        a=reduce(a,t)
    if(b>=GF):
        b=reduce(b,t)

    gp = galois.GF(GF)
    x=gp(a)
    y=gp(b)
    z=x*y
    updateresult(str(z))

def div(a,b,GF):
    t=GF
    GF=2 ** (int(GF))
    if (b==0):
        popupmsg("Can not divide by 0")
    if(a>=GF):
        a=reduce(a,t)
    if(b>=GF):
        b=reduce(b,t)

    gp = galois.GF(GF)  
    x=gp(a)
    y=gp(b)
    z=x/y
    updateresult(str(z))

def inv(a,GF):
    t=GF
    GF=2 ** (int(GF))
    if(a>=GF):
        a=reduce(a,t)
    gp = galois.GF(GF)  
    z=gp(1)/gp(a)
    updateresult(str(z))


